def exercise_1_1(train_df, test_df):

    Y = train_df.iloc[:, 3].to_numpy()
    X = train_df.iloc[:, 2].to_numpy()

    #print(Y)
    print("Questão 1.1")


    Ds=[2,3,4,5,6]

    for D in Ds:
        X_final=[]
        B_final=[]
        base=np.linspace(0,500,100)
        for element in X:
            X_final.append([element**i for i in range(D)])
        for element in base:
            B_final.append([element**i for i in range(D)])

        LR = LinearRegression()
        reg=LR.fit(np.array(X_final), Y)
        print("Modelo D = ",D-1)
        print(reg)


        plt.plot(base, np.dot(B_final, reg), '-r', label='pred_function')
        plt.scatter(X, Y, cmap=plt.cm.Spectral)
        plt.show()

        print("negative log loss:")
        print(log_loss(Y, LR.predict(X_final)))



def exercise_1_2(train_df, test_df):

    Y = train_df.iloc[:, 3].to_numpy()
    X = train_df.iloc[:, 1:2].to_numpy()

    #print(Y)
    print("Questão 1.2")


    Ds=[2,3,4,5,6]

    for D in Ds:
        X_final=[]
        base=np.linspace(0,500,100)
        for element in X:
            aux=[]
            for e in element:
                aux+=[e**i for i in range(D)]
            X_final.append(aux)


        LR = LinearRegression()
        reg=LR.fit(np.array(X_final), Y)
        print("Modelo D = ",D-1)
        print(reg)


        print("negative log loss:")
        print(log_loss(Y, LR.predict(X_final)))


